import React from "react";
import styled from "styled-components";
import { PageHero } from "../components";
import aboutImg from "../assets/hero-bcg.jpeg";

const AboutPage = () => {
  return (
    <main>
      <PageHero></PageHero>
      <Wrapper>
        <img src={aboutImg} alt="Product example" />
        <article>
          <div className="title">
            <h2>Hikayemiz</h2>
            <div className="underline"></div>
          </div>
          <p>
            1998 yılında "En yeni iletişim ve bilgisayar teknolojilerini
            kullanarak müşterilerine dünya standartlarında çözümler sunmak"
            ilkesiyle yola çıkan kubilaybzk.com, geçen 2 aylık süreçte kendini 
            en kısa sürede react ile uygulama geliştirecek duruma getirmiş biri olmayı
            başarmıştır. 2021 Aralık  yılında alan adları standartlarını belirleyen ve
            denetleyen tek otorite ICANN'e akredite olan isimtescil.net, 2010
            yılından beri Türkiye'nin en büyük 500 bilişim şirketi arasında yer
            almakta ve kurulduğu günden buyana 1 milyonun üzerinde domain kaydı
            ve 200 bininin üzerinde barındırma hizmetne ev sahipliği yapmıştır.
          </p>
        </article>
      </Wrapper>
    </main>
  );
};

const Wrapper = styled.section`
  display: grid;
  gap: 4rem;
  img {
    width: 100%;
    display: block;
    border-radius: var(--radius);
    height: 500px;
    object-fit: cover;
  }
  p {
    line-height: 2;
    max-width: 45em;
    margin: 0 auto;
    margin-top: 2rem;
    color: var(--clr-grey-5);
  }
  .title {
    text-align: left;
  }
  .underline {
    margin-left: 0;
  }
  @media (min-width: 992px) {
    grid-template-columns: 1fr 1fr;
  }
`;
export default AboutPage;
